import React from 'react';
import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
global["aaa"] = 1
class HomeScreen extends React.Component {

  state = {
    name: '',
  };
//props に自動で名ナビゲーションがはいる
onPress = () => this.props.navigation.navigate('Chat', { name: this.state.name,chinko:"sssss"});
onPress2 = () => {alert("aaa");this.props.navigation.navigate('Appl', { name: this.state.name })};
onPress3 = () => this.props.navigation.navigate('Chat', { name: this.state.name });


//name toiu namaede ataino shutoku
  onChangeText = name => this.setState({ name });

  render() {
    return (
      <View>
        <Text style={styles.title}>Enter your name:</Text>
        <TextInput
          style={styles.nameInput}
          placeHolder="Boris Johnson"
          onChangeText={this.onChangeText}
          //set parameter name to chat
          value={this.state.name}
        />
        <TouchableOpacity onPress={this.onPress}>
          <Text style={styles.buttonText}>Next</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={this.onPress2}>
          <Text style={styles.buttonText}>APPl</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress3={this.onPress}>
          <Text style={styles.buttonText}>Next</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

const offset = 24;
const styles = StyleSheet.create({
  title: {
    marginTop: offset,
    marginLeft: offset,
    fontSize: offset,
  },
  nameInput: {
    height: offset * 2,

    margin: offset,
    paddingHorizontal: offset,
    borderColor: '#111111',
    borderWidth: 1,
  },
  buttonText: {
    marginLeft: offset,
    fontSize: offset,
  },
});

export default HomeScreen;
